import React from "react";
import '../App.css'
import ProjectCard from "./ProjectCard";
import ResumeSection from "./Resume";
import CardPort from '../components/Cardport'
import Costantino from '../costantinosanna.png'
import Giuseppe from '../giuseppedt.png'
import Colon from '../colon.png'
import enter from '../enter.jpg'
import { Link } from "react-router-dom";

function Portfolio() {
    const projects = [
        {
            id: 1,
            title: "Costantino Sanna",
            image: Costantino,
            description:
                "È con sommo gaudio che posso finalmente presentare il mio nuovo progetto. Prima esperienza di learning on the job ed è stato moolto divertente. Dando vita a questo spazietto ho studiato #tailwindCSS. Costantino è un visionario, ha posti nella mente dove rifugiarsi, sogni in cui riconoscersi, ricordi in cui specchiarsi, storie che gli scivolano dalle dita. Durante la realizzazione del sito gli ho fatto un'ora di intervista e in alcune cose ho scoperto che siamo simili. Ama follemente la sua terra: la Sardegna, della quale sembra amico e custode, odia la noia e la banalità. Io nel mio piccolo spero di avergli costruito una cuccia virtuale che un poco gli somigli.",
            technologies: ["Tailwind CSS"],
            liveUrl: "https://www.costantinosanna.it",
            // githubUrl: "https://github.com/example/project-1",
        },
        {
            id: 2,
            title: "Colostomia",
            image: Colon,
            description:
                "Un giovane programmatore, durante l'ennesima giornata in ufficio, preda della quotidiana noia, provò a reindirizzare nell'interfaccia audio del suo sistema Linux un file di testo con il quale aveva giornalmente a che fare. Generò così un rumore sgradevole che ricordava quello di una vecchia stampante...",
            technologies: ["Html5", "CSS3", "Bootstrap"],
            liveUrl: "https://colostomia.altervista.org",
            // githubUrl: "https://github.com/example/project-2",
        },
        {
            id: 3,
            title: "Giuseppe De Tommasi",
            image: Giuseppe,
            description:
                "Il primo ospite del mio progetto di rispolvero dell'arte è Giuseppe de Tommasi, un Maestro Cartapestaio leccese che ha portato i suoi presepi in tutto il mondo, dando fregio al nostro paese e alla nobile arte della cartapesta.",
            technologies: ["Html5", "CSS3", "Bootstrap", "Javascript", "JQuery", "PHP"],
            liveUrl: "https://www.giuseppedetommasi.it",
            // githubUrl: "https://github.com/example/project-2",
        },
    ];

    return (
        <div className="p-4 md:p-8">



            <CardPort />
            <ResumeSection />



            <hr className="m-5" />


            <div className="bg-white shadow-md shadow-zinc-700">
                <p className="w-full p-8 my-8">
                    <h1 className="font-bold text-primarypink m-5 text-xl">Rispolvero dell'arte</h1>
                    Tanti artisti svaniscono, lontani dalle vetrine virtuali: scrittori, musicisti, pittori, scultori... nascosti tra le pieghe di un paese dove i musicisti son solo quelli dei talent e i libri li scrivono gli influencer.
                    <br />Vorrei dare loro una vetrina, provare ad illuminarla, e poi chissà... la cosa più importante sarà la collaborazione e divertirsi creando insieme qualcosa. <br />
                    <br />Tutti progetti saranno in costante evoluzione, più imparo, più applico.<br /><br />
                    Stay tuned! ;)</p>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mt-5">
                    {projects.map((project) => (
                        <ProjectCard key={project.id} project={project} />
                    ))}
                </div>
            </div>
            <button>
                <Link to="/pixel">
                    <img className='mt-20 gra' src={enter} />
                </Link>
            </button>
        </div>
    );
}

export default Portfolio;
