import React, { useState } from 'react';

function Bottone() {
  const [isOpen, setIsOpen] = useState(false);
  const [isSubMenu1Open, setIsSubMenu1Open] = useState(false);
  const [isSubMenu2Open, setIsSubMenu2Open] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  const toggleSubMenu1 = () => {
    setIsSubMenu1Open(!isSubMenu1Open);
  };

  const toggleSubMenu2 = () => {
    setIsSubMenu2Open(!isSubMenu2Open);
  };


  return (
      <div className="sm:hidden">
          <div>
              <button
                  onClick={toggleMenu}
                  className="text-white font-bold focus:outline-none"
              >
                  <span className="inline-block relative mt-1 w-6">
                      <span className="block h-1 w-6 mb-1 rounded-sm bg-white"></span>
                      <span className="block h-1 w-6 mb-1 rounded-sm bg-white"></span>
                      <span className="block h-1 w-6 rounded-sm bg-white"></span>
                  </span>
              </button>

              {isOpen && (
                  <div
                  className={`${isOpen ? 'block' : 'hidden'
                    } md:flex md:items-center md:w-auto`}
                >
        
                  <div className={`$ {
                  isOpen ? 'block' : '} md:flex md:items-center md:w-auto`}>
                    <div className="md:flex-grow">
                      <a
                        href="#"
                        className="block mt-4 md:inline-block md:mt-0 mr-4"
                        onClick={toggleSubMenu1}
                      >
                        Dropdown 1
                      </a>
                      {isSubMenu1Open && (
                        <div className="md:absolute right-0 mt-2 py-2 bg-white rounded-md shadow-lg">
                          <a
                            href="#"
                            className="block px-4 py-2 hover:bg-gray-100"
                          >
                            Voce 1
                          </a>
                          <a
                            href="#"
                            className="block px-4 py-2 hover:bg-gray-100"
                          >
                            Voce 2
                          </a>
                          <a
                            href="#"
                            className="block px-4 py-2 hover:bg-gray-100"
                          >
                            Voce 3
                          </a>
                        </div>
                      )}
                      <a
                        href="#"
                        className="block mt-4 md:inline-block md:mt-0 mr-4"
                        onClick={toggleSubMenu2}
                      >
                        Dropdown 2
                      </a>
                      {isSubMenu2Open && (
                        <div className="md:absolute right-0 mt-2 py-2 bg-white rounded-md shadow-lg">
                          <a
                            href="#"
                            className="block px-4 py-2 hover:bg-gray-100"
                          >
                            Voce 1
                          </a>
                          <a
                            href="#"
                            className="block px-4 py-2 hover:bg-gray-100"
                          >
                            Voce 2
                          </a>
                          <a
                            href="#"
                            className="block px-4 py-2  hover:bg-gray-100"
                          >
                            Voce 3
                          </a>
                        </div>
        
                      )}
                    </div>
                  </div>
                </div>
              )}

          </div>
      </div>

  )

}

export default Bottone;
