import React from 'react';
import { useState } from "react";
import {Link} from 'react-router-dom'


function Bottone() {
  const handleClick = () => {
    const element = document.getElementById('my-section');
    element.scrollIntoView({ behavior: 'smooth' });
  };

  return (
<div className='sm:hidden'>
<button
onClick={() => setIsOpen(!isOpen)}
className="text-white font-bold focus:outline-none"
>
<svg
  className="h-6 w-6"
  fill="none"
  stroke="currentColor"
  viewBox="0 0 24 24"
  xmlns="http://www.w3.org/2000/svg"
>
  <path
    strokeLinecap="round"
    strokeLinejoin="round"
    strokeWidth={2}
    d="M4 6h16M4 12h16M4 18h16"
  />
</svg>
</button>

{isOpen && (
          <div className="absolute top-20 right-0 w-full bg-slate-50 p-4">
            <ul className="flex flex-col space-y-4 text-zinc-800 font-bold ">
            <li>
            <Link to="/pagina1">Home</Link>
          </li>
          <li>
            <Link to="/about">About</Link>
          </li>
          <li>
            <Link to="/portfolio">Portfolio</Link>
          </li>
          <li>
            <Link to="/libro">il mio libro</Link>
          </li>
            </ul>
          </div>
        )}



        

</div>

)

}

export default Bottone;
