import React, { useState } from 'react';
import css from '../css.jpg'
import html from '../html.jpg'
import javascript from '../javascript.jpg'
import jquery from '../jquery.jpg'


function Modal() {
  const [isOpen, setIsOpen] = useState(false);
  const [selectedImage, setSelectedImage] = useState('');

  const handleOpenModal = (image) => {
    setSelectedImage(image);
    setIsOpen(true);
  };

  const handleCloseModal = () => {
    setSelectedImage('');
    setIsOpen(false);
  };

  return (
    <div>
      <div className="flex flex-row justify-center mt-10">
        <div className="m-2 w-1/4">
          <img src={html} onClick={() => handleOpenModal({html})} />
        </div>
        <div className="m-2 w-1/4">
          <img src={html} onClick={() => handleOpenModal({html})} />
        </div>
        <div className="m-2 w-1/4">
          <img src={html} onClick={() => handleOpenModal('image3.jpg')} />
        </div>
        <div className="m-2 w-1/4">
          <img src={html} onClick={() => handleOpenModal('image4.jpg')} />
        </div>
      </div>

      {isOpen && (
        <div className="fixed top-0 left-0 right-0 bottom-0 bg-black bg-opacity-75 flex items-center justify-center z-50">
          <div className="relative max-w-3xl w-full">
            <button onClick={handleCloseModal} className="absolute top-0 right-0 m-4 text-white font-bold focus:outline-none">
              <svg className="h-6 w-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
            <img src={selectedImage} className="mx-auto" />
          </div>
        </div>
      )}
    </div>
  );
}

export default Modal;
