import React from "react";
import '../App.css'
import ProjectCard from "./ProjectCard";

function Portfolio() {
    const projects = [
        {
            id: 1,
            title: "Progetto 1",
            image: "/images/project-1.jpg",
            description:
                "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nullam eget gravida mi, sit amet elementum urna. Sed rhoncus tortor non mi bibendum interdum. Nam efficitur urna purus, ut tempor eros elementum vitae. Praesent tincidunt euismod velit, at sagittis odio tincidunt sit amet. Integer efficitur risus nec elit dapibus, eu consequat enim imperdiet.",
            technologies: ["React", "Tailwind CSS"],
            liveUrl: "https://www.example.com",
            githubUrl: "https://github.com/example/project-1",
        },
        {
            id: 2,
            title: "Progetto 2",
            image: "/images/project-2.jpg",
            description:
                "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nullam eget gravida mi, sit amet elementum urna. Sed rhoncus tortor non mi bibendum interdum. Nam efficitur urna purus, ut tempor eros elementum vitae. Praesent tincidunt euismod velit, at sagittis odio tincidunt sit amet. Integer efficitur risus nec elit dapibus, eu consequat enim imperdiet.",
            technologies: ["React", "Node.js", "MongoDB"],
            liveUrl: "https://www.example.com",
            githubUrl: "https://github.com/example/project-2",
        },
        {
            id: 3,
            title: "Progetto 3",
            image: "/images/project-3.jpg",
            description:
                "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nullam eget gravida mi, sit amet elementum urna. Sed rhoncus tortor non mi bibendum interdum. Nam efficitur urna purus, ut tempor eros elementum vitae. Praesent tincidunt euismod velit, at sagittis odio tincidunt sit amet. Integer efficitur risus nec elit dapibus, eu consequat enim imperdiet.",
            technologies: ["HTML", "CSS", "JavaScript"],
            liveUrl: "https://www.example.com",
            githubUrl: "https://github.com/example/project-3",
        },
    ];

    return (
        <div className="p-4 md:p-8">
            <div className="flex md:flex-col md:my-40 md:mx-10 bg-neutral-100 p-5 shadow-2xl">

            <h1 className="text-3xl font-bold mb-2">Storia lavorativa</h1>
                <p>
                    Ho iniziato a lavorare giovanissima, in un banco di abbigliamento usato del mercato di Santa Giulia. Mi sono trovata poi a ricoprire diversi ruoli, imparando ad essere versatile e aperta alle novità.
                    Non ho mai rifiutato ruoli di grande responsabilità e riconosco in me un'attitudine più da leader che da capo, abbastanza infruttuosa di questi tempi.
                    Ho svolto una moltitudine di lavori fisici, dalla barista notturna alla saldatrice e ho moderatamente amato tutto ciò che ho fatto (amo soprattutto le spalle da rugbista e i bicipiti che mi sono rimasti in lascito).
                    <br />


                    Attualmente, in un'impavida virata, mi sono gettata nello studio approfondito dello Sviluppo Web, riprendendo così in mano una passione nata nell'adolescenza e un mondo che avevo lungamente esplorato da autodidatta.
                    La cosa più bella è stata ritrovare lo stesso divertimento.
                    <br />
                    Stay tuned!
                </p>


                
            </div>



            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mt-20">
                {projects.map((project) => (
                    <ProjectCard key={project.id} project={project} />
                ))}
            </div>
        </div>
    );
}

export default Portfolio;
