import React, { useState } from 'react';
import css from '../css.jpg'
import html from '../html.jpg'
import javascript from '../javascript.jpg'
import jquery from '../jquery.jpg'
import styles from './footericons.css'


function Modal() {
  const [isOpen, setIsOpen] = useState(false);
  const [selectedImage, setSelectedImage] = useState('');

  const handleOpenModal = (image) => {
    setSelectedImage(image);
    setIsOpen(true);
  };

  const handleCloseModal = () => {
    setSelectedImage('');
    setIsOpen(false);
  };

  return (
    <div>
      <div className="flex flex-row justify-center mt-10">

        <button className="bg-primarypink hover:bg-zinc-700 text-gray-50 font-bold rounded-lg m-1 w-1/4 text-xs md:text-md shadow-md shadow-zinc-900 transition-colors duration-300" onClick={() => handleOpenModal(html)}>
          Html5
        </button>
        <button className="bg-primarypink hover:bg-zinc-700 text-gray-50 font-bold rounded-lg  w-1/4 text-xs md:text-md shadow-md shadow-zinc-900 transition-colors duration-300" onClick={() => handleOpenModal(css)}>
          CSS3
        </button>
        <button className="bg-primarypink hover:bg-zinc-700 text-gray-50 font-bold  rounded-lg w-1/4 text-xs md:text-md shadow-md shadow-zinc-900 transition-colors duration-300" onClick={() => handleOpenModal(javascript)}>
          Javascript
        </button>
        <button className="bg-primarypink hover:bg-zinc-700 text-gray-50 font-bold rounded-lg w-1/4 text-xs md:text-md shadow-md shadow-zinc-900 transition-colors duration-300" onClick={() => handleOpenModal(jquery)}>
          JQuery
        </button>

      </div>

      {isOpen && (
        <div className="fixed top-0 left-0 right-0 bottom-0 bg-black bg-opacity-75 flex items-center justify-center z-50">
          <div className="relative max-w-3xl w-full">
            <button onClick={handleCloseModal} className="absolute top-0 right-0 m-4 text-black font-bold focus:outline-none">
              <svg className="h-6 w-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
            <img src={selectedImage} className="mx-auto" />
          </div>
        </div>
      )}
    </div>
  );
}

export default Modal;
