import React from 'react';
import alex from '../alex3.jpg'
import { useEffect, useState } from "react";

const Card = () => {
  const [show, setShow] = useState(false);

  useEffect(() => {
    setShow(true);
  }, []);
  
  return (
    <div className={`flex flex-wrap items-center justify-center rounded-lg mb-10 ${
      show ? "card slide-in" : ""
    }`}>
      <div className="w-full md:w-1/2 rounded overflow-hidden shadow-md shadow-zinc-900 p-20 provabg border-2 border-zinc-900">
        <img className="w-full rounded-full shadow-lg border-2 border-gray-50 drop-shadow-2xl grayscale" src={alex} alt="Random" />
        <div className="text-xl mt-5 rounded-md shadow-md shadow-zinc-900 bg-white"><span>Alessia Gaudioso</span>
          <p className="text-primarypink text-base">
            Web Developer<br />
            Writer
          </p>
          </div>

      </div>
      <div className="w-full md:w-1/2 md:ml-4">
        <div className="px-6 py-4 bg-white mt-10 rounded-md shadow-md shadow-zinc-900">
          
          <div className="px-6 py-4">
            <h1 className='mb-5 font-bold'>Site built with:</h1>
            <span className="inline-block bg-primarypink rounded-full px-3 py-1 text-sm font-semibold text-gray-50 m-2 shadow-md shadow-zinc-900">ReactJS</span>
            <span className="inline-block bg-primarypink rounded-full px-3 py-1 text-sm font-semibold text-gray-50 shadow-md shadow-zinc-900">Tailwind CSS</span>
          </div>
        </div>
      </div>
    </div>
  );
};
  
export default Card;
