import { Fragment, useState } from 'react';
import php from './submit-form.php'


function MyForm() {
  const [showModal, setShowModal] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    setShowModal(true);
  }

  return (
 
    <div className='flex flex-col sm:flex-row justify-center my-20 shadow-md shadow-zinc-900 p-20 m-10 '>
        <hr />
        <div className=''>
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label htmlFor="name" className="block mb-2">Nome</label>
          <input type="text" name="name" id="name" className="w-full border py-2 px-3" />
        </div>
        <div className="mb-4">
          <label htmlFor="email" className="block mb-2">Email</label>
          <input type="email" name="email" id="email" className="w-full border py-2 px-3" />
        </div>
        <div className="mb-4">
          <label htmlFor="message" className="block mb-2">Messaggio</label>
          <textarea name="message" id="message" rows="5" className="w-full border py-2 px-3" />
        </div>
        <button type="submit" className="inline-block bg-primarypink rounded-full px-3 py-1 text-sm font-semibold text-gray-50 mb-2 mt-1 mr-1 shadow-md shadow-zinc-900">Invia</button>
      </form>
      </div>
      {showModal && (
        <div className="fixed top-0 left-0 w-full h-full bg-gray-500 opacity-50 z-10">
        <div className="fixed top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 z-20">
          <div className="bg-white rounded-lg p-4 shadow-lg">
            <p className="mb-4">Inviato!</p>
            <div className="flex justify-center">
              <button onClick={() => setShowModal(false)} className="inline-block bg-primarypink rounded-full px-3 py-1 text-sm font-semibold text-gray-50 mb-2 mt-1 mr-1 shadow-md shadow-zinc-900">
                Grazie, a presto! ;)
              </button>
              
            </div>
          </div>
        </div>
        </div>
        
         
      )}
     
    </div>

  );
}

export default MyForm;
