import React, { useState, useEffect, useRef } from "react";

import { Link } from 'react-router-dom';



const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const closeDropdown = () => {
    setIsOpen(false);
  };

 
  const menuRef = useRef();

  useEffect(() => {
    const handleOutsideClick = (event) => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setMenuVisible(false);
      }
    };
    document.addEventListener("mousedown", handleOutsideClick);
    return () => {
      document.removeEventListener("mousedown", handleOutsideClick);
    };
  }, [menuRef]);
  return (
    <div className="block sm:hidden">
      <button
        onClick={toggleDropdown}
        className="text-slate-50 hover:text-pink-500 focus:outline-none"
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          className="h-6 w-6"
          fill="none"
          viewBox="0 0 24 24"
          stroke="currentColor"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M4 6h16M4 12h16M4 18h16"
          />
        </svg>
      </button>
      {isOpen && (
        <div ref={menuRef} className="absolute top-16 right-0 w-40 bg-gray-50 border shadow-md shadow-zinc-900 z-10">
          <ul>
            <li className="border-b border-gray-300 shadow-sm shadow-zinc-700 hover:shadow-pink-500">
              <Link
                to="/*"
                onClick={closeDropdown}
                className="block px-4 py-2 text-gray-800"
              >
                Home
              </Link>
            </li>
            <li className="border-b border-gray-300 shadow-sm shadow-zinc-700 hover:shadow-pink-500">
              <Link
                to="/portfolio"
                onClick={closeDropdown}
                className="block px-4 py-2 text-gray-800"
              >
                Portfolio
              </Link>
            </li>
            <li className="border-b border-gray-300 shadow-sm shadow-zinc-700 hover:shadow-pink-500">
              <Link
                to="/libro"
                onClick={closeDropdown}
                className="block px-4 py-2 text-gray-800"
              >
                Il mio libro
              </Link>
            </li>
            <li className="border-b border-gray-300 shadow-sm shadow-zinc-700 hover:shadow-pink-500">
              <Link
                to="/contatti"
                onClick={closeDropdown}
                className="block px-4 py-2 text-gray-800"
              >
                Contatti
              </Link>
            </li>
          </ul>
          <ul>
            <li>
              <a
                href="https://evitolaforma.altervista.org"
                target={'_blank'}
                onClick={closeDropdown}
                className="block px-4 py-2 text-gray-800"
              >
                Blog
              </a>
            </li>
          </ul>
        </div>
      )}
    </div>
  );
};

export default Navbar;
