import React from 'react';
import { useState } from "react";
import logo from '../vitamina4.jpg'
import {Link} from 'react-router-dom'
import Navbar from './dropdown';


function Header() {
  const [isOpen, setIsOpen] = useState(false);

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  return (
    <header className="bg-zinc-800 h-30 flex justify-between items-center px-4 sm:px-8 shadow-md shadow-pink-700">
      <div className="flex items-center">
      <Link to="/*"><img src={logo} className="w-16 m-5 rounded-full shadow-md  hover:shadow-white hover:scale-105" /></Link>
        <span className="text-slate-50 font-bold text-lg">Indiefetto</span>
      </div>
      <nav className="hidden sm:block">
        <ul className="flex space-x-6 text-slate-50 font-bold">
          <li className="hover:text-primarypink">
            <Link to="/*">Home</Link>
          </li>
          <li className="hover:text-primarypink">
            <Link to="/portfolio">Portfolio</Link>
          </li>
          <li className="hover:text-primarypink">
            <Link to="/libro">il mio libro</Link>
          </li>
        </ul>
      </nav>
     <Navbar />

      <div className="hidden sm:block">
       <a href='https://evitolaforma.altervista.org/' target={'_blank'}><button className="bg-white text-primarypink font-bold py-2 px-4 rounded-lg shadow-md shadow-white">Blog</button></a> 
      </div>
    </header>
  );
}

export default Header;
