import React from "react";
import '../App.css'
import ProjectCard from "./ProjectCard";
import Modal from "./Modal";
import Immage from "./immagine";
// import css from '../css.jpg'
// import html from '../html.jpg'
// import javascript from '../javascript.jpg'
// import jquery from '../jquery.jpg'
import ResumeSection from "./Resume";
import CardPort from '../components/Cardport'
import Costantino from '../costantinosanna.png'
import giuseppe from '../giuseppedt.png'
import colon from '../colon.png'

function Portfolio() {
    const projects = [
        {
            id: 1,
            title: "Progetto 1",
            image: Costantino,
            description:
                "È con sommo gaudio che posso finalmente presentare il mio nuovo progetto. Prima esperienza di learning on the job ed è stato moolto divertente. Progettando questo spazietto ho studiato #tailwindCSS e diciamo che mi sembra di averlo quasi imparato per bene. A parte delle modifiche ai testi e una riordinata necessaria al codice, direi che se vi va potete iniziare a fare un salto! Costantino è un visionario, ha posti nella mente dove rifugiarsi, sogni in cui riconoscersi, ricordi in cui specchiarsi, storie che gli scivolano dalle dita. Durante la realizzazione del sito gli ho fatto un'ora di intervista e in alcune cose ho scoperto che siamo simili. Ama follemente la sua terra: la Sardegna, della quale sembra amico e custode, odia la noia e la banalità. Io nel mio piccolo spero di avergli costruito una cuccia virtuale che un poco gli somigli.",
            technologies: ["React", "Tailwind CSS"],
            liveUrl: "https://www.costantinosanna.it",
            // githubUrl: "https://github.com/example/project-1",
        },
        {
            id: 2,
            title: "Progetto 2",
            image: "/images/project-2.jpg",
            description:
                "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nullam eget gravida mi, sit amet elementum urna. Sed rhoncus tortor non mi bibendum interdum. Nam efficitur urna purus, ut tempor eros elementum vitae. Praesent tincidunt euismod velit, at sagittis odio tincidunt sit amet. Integer efficitur risus nec elit dapibus, eu consequat enim imperdiet.",
            technologies: ["React", "Node.js", "MongoDB"],
            liveUrl: "https://www.example.com",
            githubUrl: "https://github.com/example/project-2",
        },
        {
            id: 3,
            title: "Progetto 3",
            image: "/images/project-3.jpg",
            description:
                "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nullam eget gravida mi, sit amet elementum urna. Sed rhoncus tortor non mi bibendum interdum. Nam efficitur urna purus, ut tempor eros elementum vitae. Praesent tincidunt euismod velit, at sagittis odio tincidunt sit amet. Integer efficitur risus nec elit dapibus, eu consequat enim imperdiet.",
            technologies: ["HTML", "CSS", "JavaScript"],
            liveUrl: "https://www.example.com",
            githubUrl: "https://github.com/example/project-3",
        },
    ];

    return (
        <div className="p-4 md:p-8">



            <CardPort />
            <Modal />
            <ResumeSection />



            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mt-20">
                {projects.map((project) => (
                    <ProjectCard key={project.id} project={project} />
                ))}
            </div>
        </div>
    );
}

export default Portfolio;
