import React, { useState } from 'react';

function Bottone() {
  const [isOpen, setIsOpen] = useState(false);
  const [isSubMenu1Open, setIsSubMenu1Open] = useState(false);
  const [isSubMenu2Open, setIsSubMenu2Open] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  const toggleSubMenu1 = () => {
    setIsSubMenu1Open(!isSubMenu1Open);
  };

  const toggleSubMenu2 = () => {
    setIsSubMenu2Open(!isSubMenu2Open);
  };


  return (
      <div className="sm:hidden">
          <div>
              <button
                  onClick={() => setIsOpen(!isOpen)}
                  className="text-white font-bold focus:outline-none"
              >
                  <span className="inline-block relative mt-1 w-6">
                      <span className="block h-1 w-6 mb-1 rounded-sm bg-white"></span>
                      <span className="block h-1 w-6 mb-1 rounded-sm bg-white"></span>
                      <span className="block h-1 w-6 rounded-sm bg-white"></span>
                  </span>
              </button>

              {isOpen && (
                  <div className="absolute top-20 right-0 w-full bg-gray-100 p-4 ">
                      <ul className="flex flex-col space-y-4 text-black font-bold">
                          <li>
                              <a href="/">1</a>
                          </li>
                          <li>
                              <a href="/">2</a>
                          </li>
                          <li>
                              <a href="/">3</a>
                          </li>
                      </ul>
                  </div>
              )}

          </div>
      </div>

  )

}

export default Bottone
