import { useState } from 'react';
import { Link } from 'react-router-dom';

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const closeDropdown = () => {
    setIsOpen(false);
  };

  return (
    <div className="block sm:hidden">
      <button
        onClick={toggleDropdown}
        className="text-slate-50 hover:text-pink-500 focus:outline-none"
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          className="h-6 w-6"
          fill="none"
          viewBox="0 0 24 24"
          stroke="currentColor"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M4 6h16M4 12h16M4 18h16"
          />
        </svg>
      </button>
      {isOpen && (
        <div className="absolute top-16 right-0 w-40 bg-gray-50 border shadow-xl z-10">
          <ul>
            <li className="border-b border-gray-300 shadow-md shadow-pink-500">
              <Link
                to="/*"
                onClick={closeDropdown}
                className="block px-4 py-2 text-gray-800 hover:bg-zinc-700 hover:text-gray-50"
              >
                Home
              </Link>
            </li>
            <li className="border-b border-gray-300 shadow-md shadow-pink-500">
              <Link
                to="/portfolio"
                onClick={closeDropdown}
                className="block px-4 py-2 text-gray-800 hover:bg-primarypink hover:text-gray-50"
              >
                Portfolio
              </Link>
            </li>
            <li className="border-b border-gray-300 shadow-md shadow-pink-500">
              <Link
                to="/libro"
                onClick={closeDropdown}
                className="block px-4 py-2 text-gray-800 hover:bg-primarypink hover:text-gray-50"
              >
                Il mio libro
              </Link>
            </li>
          </ul>
          <ul>
            <li>
              <a
                href="https://evitolaforma.altervista.org"
                target={'_blank'}
                onClick={closeDropdown}
                className="block px-4 py-2 text-gray-800 hover:bg-primarypink hover:text-gray-50"
              >
                Blog
              </a>
            </li>
          </ul>
        </div>
      )}
    </div>
  );
};

export default Navbar;
